INSERT INTO `locales_source` VALUES ('3336','','default','manage display','','7.10'),('3337','','default','No content types available. <a href="@link">Add content type</a>.','','7.10'),('3338','','default','The human-readable name of this content type. This text will be displayed as part of the list on the <em>Add new content</em> page. It is recommended that this name begin with a capital letter and contain only letters, numbers, and spaces. This name must be unique.','','7.10'),('3339','','default','Describe this content type. The text will be displayed on the <em>Add new content</em> page.','','7.10'),('3340','','default','Preview before submitting','','7.10'),('3341','','default','This text will be displayed at the top of the page when creating or editing content of this type.','','7.10'),('3342','','default','Users with the <em>Administer content</em> permission will be able to override these options.','','7.10'),('3343','','default','Display author and date information.','','7.10'),('3344','','default','Author username and publish date will be displayed.','','7.10'),('3345','','default','Invalid machine-readable name. Enter a name other than %invalid.','','none'),('3346','','default','%type is used by 1 piece of content on your site. If you remove this content type, you will not be able to edit the %type content and it may not display correctly.','','none'),('3347','','default','%type is used by @count pieces of content on your site. If you remove %type, you will not be able to edit the %type content and it may not display correctly.','','none'),('3348','','default','Publish selected content','','7.10'),('3349','','default','Unpublish selected content','','7.10'),('3350','','default','Promote selected content to front page','','7.10'),('3351','','default','Demote selected content from front page','','7.10'),('3352','','default','Make selected content sticky','','7.10'),('3353','','default','Make selected content not sticky','','7.10'),('3354','','default','Del